import React, { useState } from 'react';
import UrlForm from './components/UrlForm';
import ResultsDisplay from './components/ResultsDisplay';
import LoadingSpinner from './components/LoadingSpinner';
import { verifyUrl } from './services/api';
import './styles/App.css';

function App() {
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleUrlSubmit = async (url) => {
    setLoading(true);
    setError(null);
    setResults(null);

    try {
      const data = await verifyUrl(url);
      setResults(data);
    } catch (err) {
      setError(err.message || 'An error occurred while verifying the URL');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>URL Trustworthiness Verifier</h1>
        <p>Check any URL for safety threats, SSL certificates, and phishing attempts</p>
      </header>

      <main className="app-main">
        <UrlForm onSubmit={handleUrlSubmit} />

        {loading && <LoadingSpinner />}

        {error && (
          <div className="error-message">
            <h3>Error</h3>
            <p>{error}</p>
          </div>
        )}

        {results && <ResultsDisplay results={results} />}
      </main>

      <footer className="app-footer">
        <p>Powered by Google Safe Browsing, VirusTotal, SSL Labs, and PhishTank</p>
      </footer>
    </div>
  );
}

export default App;
